import React from "react";
import restaurantData from '../data/restaurants.json';
import locationData from '../data/location.json';
import {BrowserRouter,Routes,  render } from "@testing-library/react";
import map from '../images/map.png'


class Search extends React.Component{
  constructor(){
    super();
    this.state = {
      locationSearch : '',
      restaurantData,
      locationData,
    }
  }
  handleLocationSearch = (event) =>{
    event.preventDeafault();
    this.setState({
      locationSearch : event.target.search.value
    })



  }

render() {
  return(
    <div id = "city-search">
    <form onSubmit={this.handleLocationSearch}>
      <label> Search for a location</label>
      <input type= "text" name="search" placeholder="Enter city here "/>
      <button type="submit">Explore</button>

    </form>
    {this.state.locationSearch && this.state.locationData
    ? <div id="map"><img src={map} alt ="locaton map"/></div> : null}
    {this.state.locationData && this.state.restaurantData ? <ul>{this.state.restaurantData.map(place => <li>{place.restaurant}</li>)}</ul> :null
    }

    </div>
  )
}
}

export default Search;
